import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { buscarVideojuegos } from "../actions/index";
import Videojuegos from "../Componentes/Videojuegos";
import { Pagination } from "../Componentes/Paginado";
import "../EstilosCss/BarradeBusqueda.css";
import NavBar from "./Navbar";

export default function Search() {
  const dispatch = useDispatch();
  let { name } = useParams()

  const buscarVideojuego = useSelector((state) => state.buscarVideojuegoXNombre);

  useEffect(() => {
    dispatch(buscarVideojuegos(name));
  }, [name]); 
  
  // Paginacion
  function paginar(e, num) {
    e.preventDefault();
    setPagina(num);
  }

  const [pagina, setPagina] = useState(1);
  const [videojuegosXPag] = useState(10);

  let lastCardPerPage = pagina * videojuegosXPag;
  let firtsCardPerPage = lastCardPerPage - videojuegosXPag;
  let currentPageGames = buscarVideojuego.slice(firtsCardPerPage, lastCardPerPage);

  return (
    
    <div className="search">
      
        {buscarVideojuego.length > 0 ?
        <>
        <div className="navbarrr"><NavBar/></div>
          <h1>Search Results:</h1>
          <h2>{name}</h2>
          <Videojuegos videogames={currentPageGames} />
          <Pagination
            videogamesPerPage={videojuegosXPag}
            totalVideogames={buscarVideojuego.length}
            paginate={paginar}
          />
        </>
        :  <img className = "img2" src="https://www.alpha-editorial.com/images/loading.gif" alt="Link caido"/>
        }
    </div>
  )
};
