//32)
import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getVideogameById } from "../actions/index";
import NotFound from "../components/NotFound.jsx";
import "./GameDetail.css";
import NavBar from "./Navbar";

function GameDetail(props) {
  console.log(props)
  const dispatch = useDispatch();
  const videogame = useSelector((store) => store.searchVideogameById);
  const {id} = useParams();

  useEffect(() => {
    dispatch(getVideogameById(id));
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (    
    
    <div className="containerr">
       <div><NavBar/></div>
      <div className="info">
     
        <div className="image">
        <div className="name"><h1>{videogame.name} </h1>
        <div >
            <span className="ratingg">{videogame.rating}</span>
              </div>
                </div>
                <div>

<h5 className="sub">Released Date: {videogame.released} |  Platforms: {videogame.platforms}</h5>
</div>
<div className="imgrating">
        {videogame.image === null || !videogame.image ?
              <NotFound image={"noimage"} />
              : <img className="hola" src={videogame.image} alt={videogame.name} /> }
              <div className="text">
            <h2>About this game:</h2>
            <p>{videogame.description}</p>
          </div>
        </div>
        </div>
        <div className="details">
        
          <div className="Genres">
            <div className="genres">
            It's an {videogame.genres} game ranked at {videogame.rating} points.  
            </div>
          </div>
          <div className="Platforms">
            <div className="platforms">
              <p>Play it at {videogame.platforms}.</p>
            </div>
          </div>
        </div>
      </div>
      <Link to="/home">
          <button className="button" type="submit">🡸</button>
      </Link>
    </div>    
  );
}

export default GameDetail;
