// 12)

export function getVideojuegos() {
    return function (dispatch) {
      return fetch(`http://localhost:3001/videogames`)
        .then(response => response.json())
        .then(json => {
          dispatch({ type: "GET_VIDEOJUEGOS", payload: json });
        });
    };
  }
//   20)
  
  export const ordenXCreado = (source) => (dispatch, getState) => {
    const videojuegos = getState().videojuegos.filter(function (v) {
        return v.source === source
      });
    dispatch({
      type: "ORDEN_X_CREADO",
      payload: {
        videojuegos,
        source,
      },
    });
  };

//   21)
export const ordenAsc = (type) => (dispatch, getState) => {
    const filtrado = getState().videojuegosFiltrados;
    let videojuegosOrden = []
  
      if (type === "asc_name") {
        videojuegosOrden = filtrado.sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        });
      } else if (type === "asc_rating") {
        videojuegosOrden = filtrado.sort(
          (a, b) => a.rating - b.rating
        );
      }
      dispatch({
        type: "ORDEN_ASC_RATING",
        payload: {
          videojuegosOrden,
          name: type,
        },
      });
  }
  
  
  export const ordenDesc = (type) => (dispatch, getState) => {
    const filtrado = getState().videojuegosFiltrados;
    let videojuegosOrden = []
      
      if (type === "desc_name") {
        videojuegosOrden = filtrado.sort((a, b) => {
          if (a.name < b.name) return 1;
          if (a.name > b.name) return -1;
          return 0;
        });
      } else if (type === "desc_rating") {
        videojuegosOrden = filtrado.sort(
          (a, b) => b.rating - a.rating
        );
      }
      dispatch({
        type: "ORDEN_DESC_RATING",
        payload: {
          videojuegosOrden,
          name: type,
        },
      });
  }

//23)
  export function buscarVideojuegos(name) {
    return (dispatch) =>
      fetch(`http://localhost:3001/videogames?name=${name}`)
        .then((resp) => resp.json())
        .then((json) => {
          dispatch({
            type: "BUSCAR_VIDEOJUEGOS",
            payload: json,
          });
        });
  }

  //26)
  export function crearVideojuego(obj) {
    return (dispatch) =>
      fetch("http://localhost:3001/videogame", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(obj),
      })
        .then((resp) => resp.json())
        .then((json) => {
          dispatch({
            type: "CREAR_VIDEOJUEGO",
            payload: json,
          });
        });
  }
  //30)
  export function getVideojuegoXId(id) {
    console.log(id)
    return (dispatch) =>
      fetch(`http://localhost:3001/videogame/${id}`)
        .then((resp) => resp.json())
        .then((json) => {
          dispatch({
            type: "GET_VIDEOJUEGO_X_ID",
            payload: json,
          });
        });
  }

  //ESTOS NO SE SI LOS USO:
  export const filtradoXGenero = (genres) => (dispatch, getState) => {
    let juegosFiltrados = [];
  
    if (genres === "All") {
      juegosFiltrados = getState().videojuegos;
    } else {
      juegosFiltrados = getState().videojuegos.filter((game) =>
        (game.genres).includes(genres)
      )
    };
    dispatch({
      type: "FILTRADO_X_GENERO",
      payload: {
        genres,
        videojuegoGenero: juegosFiltrados,
      },
    });
  };

  export const resetTodo = () => {
  return (dispatch) => {
    dispatch({
      type: "RESET",
    });
  };
};

