import React, { useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideojuegos, resetTodo } from "../actions/index";
import Videojuegos from "../Componentes/Videojuegos";
import { Pagination } from "../Componentes/Paginado";
import Filtro from "../Componentes/Filtro.jsx";
import "../EstilosCss/Principal.css";
import NavBar from "./Navbar";

export default function Home() {
  const dispatch = useDispatch();

  const videojuegosFiltrados = useSelector((state) => state.videojuegosFiltrados);
  const filtrado = useSelector((state) => state.filtrado);
  const orden = useSelector((state) => state.orden);
  const videojuegos = useSelector((state) => state.videojuegos);

  useEffect(() => {
    dispatch(resetTodo());
    dispatch(getVideojuegos());
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // Filtrado y Ordenado
  let todosJuegos;
  filtrado === "All" && orden === "Select"
    ? (todosJuegos = videojuegos)
    : (todosJuegos = videojuegosFiltrados);

  // Paginacion
  function paginate(e, num) {
    e.preventDefault();
    setPage(num);
  }

  const [pagina, setPagina] = useState(1);
  const [videojuegosXPag] = useState(15);

  let ultimaCardPag = pagina * videojuegosXPag;
  let primerCardPag = ultimaCardPag - videojuegosXPag;
  let juegosPagActual = todosJuegos.slice(primerCardPag, ultimaCardPag);


  return (
    <div className="home">
      <div className="gg2"><NavBar/></div>
      <div>
      <Filtro paginate={paginate} />
      </div>
      <Pagination
      videogamesPerPage={videogamesPerPage}
      totalVideogames={allVideogames.length}
      paginate={paginate}
    />
    
  
    <div>  <Videojuegos videogames={currentPageGames} /></div>
    <Pagination
      videogamesPerPage={videogamesPerPage}
      totalVideogames={allVideogames.length}
      paginate={paginate}
    />
   
  </div>
  );
};
