const initialState = {
  videojuegos: [],
  generos: [],
  buscarVideojuego: [],
  crearVideojuego: null,
  buscarVideojuegoXId: [],
  buscarVideojuegoXNombre: [],
  videojuegosFiltrados: [],
  orden: "Select",
  filtrado: "All",
};

// 13)
export default function rootReducer(state = initialState, action) {
    switch (action.type) {
      case "GET_VIDEOJUEGOS":
        return {
          ...state,
          videojuegos: action.payload,
        }
        //19)
        case "ORDEN_X_CREADO":
    return {
      ...state,
      videojuegosFiltrados: action.payload.videojuegos,
      filtrado: action.payload.source,
    };

    //22)
    case "ORDEN_ASC_NOMBRE":
        case "ORDEN_ASC_RATING":
        case "ORDEN_DESC_NOMBRE":
        case "ORDEN_DESC_RATING":
          return {
            ...state,
            videojuegosFiltrados: action.payload.videojuegosOrden,
            orden: action.payload.name,
          };
        //   24)
          case "BUSCAR_VIDEOJUEGOS":
            return {
              ...state,
              buscarVideojuegoXNombre: action.payload,
            };
            //27)
            case "CREATE_VIDEOGAME":
                return {
                  ...state,
                  crearVideojuego: action.payload,
                };    
                //28)
                case "GET_GENEROS":
                    return {
                      ...state,
                      generos: action.payload,
                    };
                    //31)
                    case "GET_VIDEOJUEGO_X_ID":
                        return {
                          ...state,
                          buscarVideojuegoXId: action.payload,
                        };
                        //ESTOS LOS AGREGO PERO NO SE SI LOS USO:
                        case "RESET":
                            return {
                              ...state,
                              videojuegos: [],
                              videojuegosFiltrados: [],
                              orden: "Select",
                              filtrado: "All",
                            }
                            case "FILTRADO_X_GENERO":
                                return {
                                  ...state,
                                  videojuegosFiltrados: action.payload.videojuegoGenero,
                                  filtrado: action.payload.genero,
                                };
              
        default:
            return state;



    }
}
