const initialState = {
    videogames: []
}

// 13)
export default function rootReducer(state = initialState, action) {
    switch (action.type) {
      case "GET_VIDEOGAMES":
        return {
          ...state,
          videogames: action.payload,
        }
        //19)
        case "ORDER_BY_CREATOR":
    return {
      ...state,
      filteredVideogames: action.payload.videogames,
      filterBy: action.payload.source,
    };
        default:
            return state;



    }
}
