//29)
import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { createVideogame, getGenres, getVideogames } from "../actions/index";

import "./Create.css";



export default function Create() {
    const dispatch = useDispatch();
    const genres = useSelector((store) => store.genres);
    const genres1 = genres.slice(0, 10)
    const genres2 = genres.slice(10, 20)
    const videogames = useSelector((store) =>store.videogames)
    let platforms = [];
    for(let i = 0; i<videogames.length;i++){
        if(platforms.includes(videogames[i].platforms))
        platforms.push(videogames[i].platforms);
    }
    platforms
    

    const [game, setGame] = useState({
        name: "",
        description: "",
        image: "",
        released: "",
        rating: 0,
        genres: [],
        platforms: [],
    });

    useEffect(() => {
        dispatch(getGenres());
        dispatch(getVideogames())
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const randomPlatforms = ["PC", "iOS", "Android", "macOS",  "PlayStation 4", "PlayStation 5", "Xbox", "PS Vita"]

    const ChangeInput = (e) => {
        if (e.target.name === "genres" || e.target.name === "platforms") {
        const arr = game[e.target.name];
        setGame({
            ...game,
            [e.target.name]: arr.concat(e.target.value),
        });
    } else {
        setGame({
            ...game,
            [e.target.name]: e.target.value,
        });
    }
    };

    

    const handleSubmit = (e) => {
        e.preventDefault();

        const obj = {
        name: game.name,
        description: game.description,
        image: game.image,
        released: game.released,
        rating: game.rating,
        genres: game.genres,
        platforms: game.platforms,
        };

        // 30) Validaciones
        if (!obj.name) {
            alert("Por favor escribe un Nombre!")
            return
        }
        if (!obj.description) {
            alert("Por favor escribe la descripciÃ³n")
            return
        }if (!obj.released) {
            alert("La fecha de lanzamiento ingresada es incorrecta")
            return
        }if (obj.rating > 5 || obj.rating < 0) {
            alert("El rating debe ser entre 0 y 5!")
            return
        }


        dispatch(createVideogame(obj));
        e.target.reset();
        alert("Videojuego creado!");
        /* dispatch(getVideogames()) */

        setGame({
            name: "",
            description: "",
            image: "",
            released: "",
            rating: 0,
            genres: [],
            platforms: [],
        });
    };

return (
    <div className="container">
        
        <form
            id="survey-form"
            className="form"
            noValidate
            onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)}
        >
            <div>
            <div>
                <div className = "titulo">
                    <h1>CreÃ¡ tu Videojuego!</h1>
                    </div>
                <div className="divTitles">
                    <div>
                        <label>Nombre:</label>
                        <input
                        className="label"
                        type="text"
                        name="name"
                        value={game.name}
                        ></input>
                    </div>
                    <div>
                        <label>DescripciÃ³n:</label>
                        <input
                        className="desc"
                        type="text"
                        name="description"
                        value={game.description}
                        contenteditable="true"
                        ></input>
                    </div>
                    <div>
                        <label >Fecha de lanzamiento:</label>
                        <input
                        className="label"
                        type="date"
                        name="released"
                        value={game.released}
                        ></input>
                    </div>
                    <div>
                        <label>Rating:</label>
                        <input
                        className="label"
                        type="number"
                        name="rating"
                        value={game.rating}
                        ></input>
                    </div>
                </div>
                <div>
                    <label>Image URL:</label>
                    <input
                    className="label"
                    type="text"
                    name="image"
                    value={game.image}
                    ></input>
                </div>
            </div>
                
                <button className="button"  type="submit">
                    Crear!
                </button>
                {/* <div><button className={styles.button}>Confirma</button></div> */}
                
            </div>
            
        </form>
       
                    <div className="generos">
                        <label className="generostitulo">GÃ©neros</label>
                        <div className="gendivs">
                            <div>
                                {genres1.map((gen) => (
                                <div key={gen.name}>
                                    <input
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                            <div>
                                {genres2.map((gen) => (
                                <div key={gen.name}>
                                    <input
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
              
                    
                
        <div className="plataformas">
                        <label className="plataformastitulo">Plataformas:</label>
                        <div >
                            
                            {platforms.map((P) => (
                                
                            <div key={P}>
                                <input
                                className="plataformas2"
                                type="checkbox"
                                name="platforms"
                                value={P}
                                ></input>
                                <label name={P}>{P}</label>
                                
                            </div>
                            ))}
                        </div>
                    </div>
        
    </div>
);
}
