// 14)

import React, { useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideojuegos, resetTodo } from "../actions/index";
import Videogames from "../components/Videogames";
import { Pagination } from "../components/Pagination";
import { Filter } from "../components/Filter.jsx";
import "../EstilosCss/Home.css";
import NavBar from "./Navbar";

export default function Home() {
  const dispatch = useDispatch();

  const filteredVideogames = useSelector((state) => state.videojuegosFiltrados);
  const filterBy = useSelector((state) => state.filtrado);
  const orderBy = useSelector((state) => state.orden);
  const videogames = useSelector((state) => state.videojuegos);

  useEffect(() => {
    dispatch(resetTodo());
    dispatch(getVideojuegos());
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // Filtrado y Ordenado
  let allVideogames;
  filterBy === "All" && orderBy === "Select"
    ? (allVideogames = videogames)
    : (allVideogames = filteredVideogames);

  // Paginacion
  function paginate(e, num) {
    e.preventDefault();
    setPage(num);
  }

  const [page, setPage] = useState(1);
  const [videogamesPerPage] = useState(15);

  let lastCardPerPage = page * videogamesPerPage;
  let firtsCardPerPage = lastCardPerPage - videogamesPerPage;
  let currentPageGames = allVideogames.slice(firtsCardPerPage, lastCardPerPage);


  return (
    <div className="home">
      <div className="gg2"><NavBar/></div>
      <div>
      <Filter paginate={paginate} />
      </div>
      <Pagination
      videogamesPerPage={videogamesPerPage}
      totalVideogames={allVideogames.length}
      paginate={paginate}
    />
    
  
    <div>  <Videogames videogames={currentPageGames} /></div>
    <Pagination
      videogamesPerPage={videogamesPerPage}
      totalVideogames={allVideogames.length}
      paginate={paginate}
    />
   
  </div>
  );
};
