import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { buscarVideojuegos } from "../actions/index";
import Videojuegos from "../components/Videojuegos";
import { Pagination } from "../components/Paginado";
import "../EstilosCss/BarradeBusqueda.css";
import NavBar from "./Navbar";

export default function Search() {
  const dispatch = useDispatch();
  let { name } = useParams()

  const searchVideogame = useSelector((state) => state.buscarVideojuegoXNombre);

  useEffect(() => {
    dispatch(buscarVideojuegos(name));
  }, [name]); 
  
  // Paginacion
  function paginate(e, num) {
    e.preventDefault();
    setPage(num);
  }

  const [page, setPage] = useState(1);
  const [videogamesPerPage] = useState(10);

  let lastCardPerPage = page * videogamesPerPage;
  let firtsCardPerPage = lastCardPerPage - videogamesPerPage;
  let currentPageGames = searchVideogame.slice(firtsCardPerPage, lastCardPerPage);

  return (
    
    <div className="search">
      
        {searchVideogame.length > 0 ?
        <>
        <div className="navbarrr"><NavBar/></div>
          <h1>Search Results:</h1>
          <h2>{name}</h2>
          <Videojuegos Videogames={currentPageGames} />
          <Pagination
            videogamesPerPage={videogamesPerPage}
            totalVideogames={searchVideogame.length}
            paginate={paginate}
          />
        </>
        :  <img className = "img2" src="https://www.alpha-editorial.com/images/loading.gif" alt="Link caido"/>
        }
    </div>
  )
};
