//29)
import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { crearVideojuego, getGeneros, getVideojuegos } from "../actions/index";
import NavBar from "./Navbar";
import "../EstilosCss/Crear.css";




export default function Create() {
    const dispatch = useDispatch();
    const genres = useSelector((store) => store.generos);
    const genres1 = genres.slice(0, 10)
    const genres2 = genres.slice(10, 20)
    const videogames = useSelector((store) =>store.videojuegos)
    let plataformas = []

       videogames?.map((v) =>{
                for(let j = 0;j<v.platforms[0].length;j++){
           if(v.platforms[j]){
plataformas.push(v.platforms[j])

           }}})    

//ACÁ ELIMINO A LAS PLATAFORMAS REPETIDAS
   plataformas = plataformas.filter(function(item, pos) {
    return plataformas.indexOf(item) == pos;    
})

// DIVIDO A LA MITAD LAS PLATAFORMAS PARA MOSTRARLAS
let plataformas1 = plataformas.slice(0,9)
let plataformas2 = plataformas.slice(9,18)

    const [game, setGame] = useState({
        name: "",
        description: "",
        image: "",
        released: "",
        rating: "",
        genres: [],
        platforms: [],
    });

    useEffect(() => {
        dispatch(getGeneros());
        dispatch(getVideojuegos())
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const randomPlatforms = ["PC", "iOS", "Android", "macOS",  "PlayStation 4", "PlayStation 5", "Xbox", "PS Vita"]

    const ChangeInput = (e) => {
        if (e.target.name === "genres" || e.target.name === "platforms") {
        const arr = game[e.target.name];
        setGame({
            ...game,
            [e.target.name]: arr.concat(e.target.value),
        });
    } else {
        setGame({
            ...game,
            [e.target.name]: e.target.value,
        });
    }
    };

    

    const handleSubmit = (e) => {
        e.preventDefault();

        const obj = {
        name: game.name,
        description: game.description,
        image: game.image,
        released: game.released,
        rating: game.rating,
        genres: game.genres,
        platforms: game.platforms,
        };

        // 30) Validaciones
        if (!obj.name) {
            alert("Por favor escribe un Nombre!")
            return
        }
        if (!obj.description) {
            alert("Por favor escribe la descripción")
            return
        }if (!obj.released) {
            alert("La fecha de lanzamiento ingresada es incorrecta")
            return
        }if (obj.rating > 5 || obj.rating < 0) {
            alert("El rating debe ser entre 0 y 5!")
            return
        }


        dispatch(crearVideojuego(obj));
        e.target.reset();
        alert("Videojuego creado!");
        /* dispatch(getVideogames()) */

        setGame({
            name: "",
            description: "",
            image: "",
            released: "",
            rating: "",
            genres: [],
            platforms: [],
        });
    };

return (
    
    <div className="container">
        <div><NavBar/></div>
        <form
            id="survey-form"
            className="form"
            noValidate
            onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)}
        >
            <div>
            <div>
                <div className = "titulo">
                    <h1>Create your Videogame!</h1>
                    </div>
                <div className="divTitles">
                    <div>
                        <label>Name:</label>
                        <input
                        className="label"
                        type="text"
                        name="name"
                        value={game.name}
                        placeholder="Name"
                        ></input>
                    </div>
                    <div>
                        <label>Description:</label>
                        <input
                        className="desc"
                        type="text"
                        name="description"
                        value={game.description}
                        placeholder="Description"
                        ></input>
                    </div>
                    <div>
                        <label >Released Date:</label>
                        <input
                        className="label"
                        type="date"
                        name="released"
                        value={game.released}
                        ></input>
                    </div>
                    <div>
                        <label>Rating:</label>
                        <input
                        className="label"
                        type="text"
                        name="rating"
                        value={game.rating}
                        placeholder="Rating"
                        ></input>
                    </div>
                </div>
                <div>
                    <label>Image URL:</label>
                    <input
                    className="label"
                    type="text"
                    name="image"
                    value={game.image}
                    placeholder="Image URL"
                    ></input>
                </div>
            </div>
                
                <button className="button"  type="submit">
                    Create!
                </button>
                {/* <div><button className={styles.button}>Confirma</button></div> */}
                
            </div>
            
        </form>
       
                    <div  onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)} className="generos">
                        <label className="generostitulo">Genres</label>
                        <div className="gendivs">
                            <div>
                                {genres1.map((gen) => (
                                <div key={gen.name}>
                                    <input
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                            <div>
                                {genres2.map((gen) => (
                                <div key={gen.name}>
                                    <input
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
              
                        <div  onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)}className="plataformas">
                        <label className="generostitulo">Platforms</label>
                        <div className="gendivs">
                            <div>
                                {plataformas1.map((p) => (
                                <div key={p}>
                                    <input
                                    className="plataformas2"
                                    type="checkbox"
                                    name="platforms"
                                    value={p}
                                    ></input>
                                    <label name={p}>{p}</label>
                                </div>
                                ))}
                            </div>
                            <div>
                                {plataformas2.map((p) => (
                                <div key={p}>
                                    <input
                                    className="plataformas2"
                                    type="checkbox"
                                    name="platforms"
                                    value={p}
                                    ></input>
                                    <label name={p}>{p}</label>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                
     </div>
);
}
