// 18)
import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getGenres, filtradoXGenero, ordenXCreado, ordenAsc, ordenDesc } from "../actions/index";
import "../EstilosCss/Filter.css";

export function Filter({paginate}) {
  const dispatch = useDispatch()
  const genres = useSelector((store) => store.generos);

  useEffect(() => {
    dispatch(getGenres());
  }, []); // eslint-disable-line react-hooks/exhaustive-deps


  // Filtrado por genre
  const handleFilter = (e) => {
    dispatch(filtradoXGenero(e.target.value))
    paginate(e, 1);
  };


  // Ordenado
  const handleOrder = (e) => {
    if (e.target.value === "asc_name" || e.target.value === "asc_rating") {
      dispatch(ordenAsc(e.target.value));
    } else if (e.target.value === "desc_name" || e.target.value === "desc_rating") {
      dispatch(ordenDesc(e.target.value));
    } else {
      dispatch(filtradoXGenero(e.target.value));
    }
  };

  // Filtrado por API/DB
  const handleCreator = (e) => {
    if (e.target.value === "Api" || e.target.value === "Created") {
      dispatch(ordenXCreado(e.target.value));
      paginate(e, 1);
    } else {
      dispatch(filtradoXGenero(e.target.value));
      paginate(e, 1);
    }
    
  };

  return (
    <div className="filter">
       <div>
        <div>Order</div>
        <select onChange={(e) => handleOrder(e)}>
          <option value="All" default>All</option>
          <option value="asc_name">(A-Z)</option>
          <option value="desc_name">(Z-A)</option>
          <option value="desc_rating">Rating  ⬆</option>
          <option value="asc_rating">Rating  ⬇</option>
       
        </select>
      </div>

      <div>
        <div>Filter by Genre</div>
        <select onChange={(e) => handleFilter(e)}>
          <option default>All</option>
          {genres.map((G) => (
            <option value={G.name}>{G.name}</option>
          ))}
        </select>
      </div>
     
      <div>
        <div>Filter by Creator</div>
        <select onChange={(e) => handleCreator(e)} >
          <option default>All</option>
          <option value="Api">From API</option>
          <option value="Created">Created</option>
        </select>
      </div>
    </div>
  );
}

export default Filter;
