//32)
import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getVideojuegoXId } from "../actions/index";
import "../EstilosCss/GameDetail.css";
import NavBar from "./Navbar";

function GameDetail(props) {
  console.log(props)
  const dispatch = useDispatch();
  const videogame = useSelector((store) => store.buscarVideojuegoXId);
  const {id} = useParams();

  useEffect(() => {
    dispatch(getVideojuegoXId(id));
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (    
    
    
    <div className="containerr">
      {videogame?
        <>
       <div><NavBar/></div>
      <div className="info">
      
     
        <div className="image">
        <div className="names"><p>{videogame.name} <p className="genress">{videogame.genres}</p></p>
        
          
        <div >
            <span className="ratingg">{videogame.rating}</span>
              </div>
                </div>
                <div>

<h5 className="sub">Released Date: {videogame.released} |  Platforms: {videogame.platforms}</h5>
</div>
<div className="imgrating">
        {videogame.image === null || !videogame.image ?
              <img className = "img" src="https://www.alpha-editorial.com/images/loading.gif" alt="Link caido"/>
              : <img className="hola" src={videogame.image} alt={videogame.name} /> }
              <div className="text">
            <h2>Detail:</h2>
            <p>{videogame.description}</p>
          </div>
        </div>
        </div>
       
      </div>
      </>
      : <img className = "img" src="https://www.alpha-editorial.com/images/loading.gif" alt="Link caido"/>
    }
     
    </div>    
  );
}

export default GameDetail;
