//29)
import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { crearVideojuego, getGeneros, getVideojuegos } from "../actions/index";
import NavBar from "./Navbar";
import "../EstilosCss/Crear.css";



export default function Create() {
    const dispatch = useDispatch();
    const generos = useSelector((store) => store.generos);
    const generos1 = generos.slice(0, 10)
    const generos2 = generos.slice(10, 20)
    const videojuegos = useSelector((store) =>store.videojuegos)
    let plataformas = []

       videojuegos?.map((v) =>{
                for(let j = 0;j<v.platforms[0].length;j++){
           if(v.platforms[j]){
plataformas.push(v.platforms[j])

           }}})    

//ACÁ ELIMINO A LAS PLATAFORMAS REPETIDAS
   plataformas = plataformas.filter(function(item, pos) {
    return plataformas.indexOf(item) == pos;    
})

// DIVIDO A LA MITAD LAS PLATAFORMAS PARA MOSTRARLAS
let plataformas1 = plataformas.slice(0,9)
let plataformas2 = plataformas.slice(9,18)

    const [j, setJ] = useState({
        name: "",
        description: "",
        image: "",
        released: "",
        rating: "",
        genres: [],
        platforms: [],
    });

    useEffect(() => {
        dispatch(getGeneros());
        dispatch(getVideojuegos())
    }, []); 

   

    const ChangeInput = (e) => {
        if (e.target.name === "genres" || e.target.name === "platforms") {
        const arr = j[e.target.name];
        setJ({
            ...j,
            [e.target.name]: arr.concat(e.target.value),
        });
    } else {
        setJ({
            ...j,
            [e.target.name]: e.target.value,
        });
    }
    };

    

    const handleSubmit = (e) => {
        e.preventDefault();

        const obj = {
        name: j.name,
        description: j.description,
        image: j.image,
        released: j.released,
        rating: j.rating,
        genres: j.genres,
        platforms: j.platforms,
        };

        // 30) Validaciones
        if (!obj.name) {
            alert("Please write the name of the videogame!")
            return
        }
        if (!obj.description) {
            alert("Please write the description of the videogame!")
            return
        }if (!obj.released) {
            alert("Please enter a release date!")
            return
        }if (obj.rating > 5 || obj.rating < 0) {
            alert("The rating must be between 0 and 5!")
            return
        }


        dispatch(crearVideojuego(obj));
        e.target.reset();
        alert("Videogame created!");
        

        setJ({
            name: "",
            description: "",
            image: "",
            released: "",
            rating: "",
            genres: [],
            platforms: [],
        });

        document.getElementById("form").reset();
    };

return (
    
    <div className="container">
        <div className="gg"><NavBar/></div>
        <form
            id="survey-form"
            className="form"
            noValidate
            onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)}
        >
            <div>
            <div>
                <div className = "titulo">
                    <h1>Create your Videogame!</h1>
                    </div>
                <div className="divTitles">
                    <div>
                        <label>Name:</label>
                        <input
                        className="label"
                        type="text"
                        name="name"
                        value={j.name}
                        placeholder="Name"
                        ></input>
                    </div>
                    <div>
                        <label>Description:</label>
                        <textarea
                        className="desc"
                        type="text"
                        name="description"
                        value={j.description}
                        placeholder="Description"
                        row="5"
                        ></textarea>
                    </div>
                    <div>
                        <label >Released Date:</label>
                        <input
                        className="label"
                        type="date"
                        name="released"
                        value={j.released}
                        ></input>
                    </div>
                    <div>
                        <label>Rating:</label>
                        <input
                        className="label"
                        type="text"
                        name="rating"
                        value={j.rating}
                        placeholder="Rating"
                        ></input>
                    </div>
                </div>
                <div>
                    <label>Image URL:</label>
                    <input
                    className="label"
                    type="text"
                    name="image"
                    value={j.image}
                    placeholder="Image URL"
                    ></input>
                </div>
            </div>
                
                <button className="button"  type="submit">
                    Create!
                </button>
                
                
            </div>
            
        </form>
       
                    <div  onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)} className="generos">
                        <label className="generostitulo">Genres</label>
                        <div className="gendivs">
                            <div id="form">
                                {generos1.map((gen) => (
                                <div key={gen.name}>
                                    <input
                                    
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                            <div>
                                {generos2.map((gen) => (
                                <div key={gen.name}>
                                    <input                                    
                                    className="generos2"
                                    type="checkbox"
                                    name="genres"
                                    value={gen.name}
                                    ></input>
                                    <label name={gen}>{gen.name}</label>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
              
                        <div  onChange={(e) => ChangeInput(e)}
            onSubmit={(e) => handleSubmit(e)}className="plataformas">
                        <label className="generostitulo">Platforms</label>
                        <div className="gendivs">
                            <div>
                                {plataformas1.map((p) => (
                                <div key={p}>
                                    <input
                                    className="plataformas2"
                                    type="checkbox"
                                    name="platforms"
                                    value={p}
                                    ></input>
                                    <label name={p}>{p}</label>
                                </div>
                                ))}
                            </div>
                            <div>
                                {plataformas2.map((p) => (
                                <div key={p}>
                                    <input
                                    className="plataformas2"
                                    type="checkbox"
                                    name="platforms"
                                    value={p}
                                    ></input>
                                    <label name={p}>{p}</label>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                
     </div>
);
}
